function setPP(slot, moveSlot, ppValue)
    -- If no slot specified, apply to all party members
    if slot == nil then
        for s = 1, emu:read8(partyCount) do
            setPP(s, moveSlot, ppValue)
        end
        return
    end
    
    -- Validate slot number
    if slot < 1 or slot > emu:read8(partyCount) then
        console:log("Invalid slot number (must be 1-" .. emu:read8(partyCount) .. ")")
        return
    end
    
    -- If no move slot specified, apply to all moves
    if moveSlot == nil then
        for m = 1, 4 do
            setOnePP(slot, m, ppValue)
        end
    else
        -- Apply to specific move slot
        setOnePP(slot, moveSlot, ppValue)
    end
end


-- Internal function to set PP for one specific move
function setOnePP(slot, moveSlot, ppValue)
    -- Set PP of a move to a specific value (default to max if not specified)
    
    -- Validate inputs
    if (moveSlot < 1 or moveSlot > 4) then
        console:log('Invalid move slot number (must be 1-4)')
        return
    end
    
    local substructSelector = {
        [ 0] = {0, 1, 2, 3},
        [ 1] = {0, 1, 3, 2},
        [ 2] = {0, 2, 1, 3},
        [ 3] = {0, 3, 1, 2},
        [ 4] = {0, 2, 3, 1},
        [ 5] = {0, 3, 2, 1},
        [ 6] = {1, 0, 2, 3},
        [ 7] = {1, 0, 3, 2},
        [ 8] = {2, 0, 1, 3},
        [ 9] = {3, 0, 1, 2},
        [10] = {2, 0, 3, 1},
        [11] = {3, 0, 2, 1},
        [12] = {1, 2, 0, 3},
        [13] = {1, 3, 0, 2},
        [14] = {2, 1, 0, 3},
        [15] = {3, 1, 0, 2},
        [16] = {2, 3, 0, 1},
        [17] = {3, 2, 0, 1},
        [18] = {1, 2, 3, 0},
        [19] = {1, 3, 2, 0},
        [20] = {2, 1, 3, 0},
        [21] = {3, 1, 2, 0},
        [22] = {2, 3, 1, 0},
        [23] = {3, 2, 1, 0},
    }
    local address = getSlotAddress(slot)
    local mon = readBoxMon(address)
    local key = mon.otId ~ mon.personality
    local ss0 = {}
    local ss1 = {}
    local ss2 = {}
    local ss3 = {}

    local pSel = substructSelector[mon.personality % 24]
    for i = 0, 2 do
        ss0[i] = emu:read32(address + 32 + pSel[1] * 12 + i * 4) ~ key
        ss1[i] = emu:read32(address + 32 + pSel[2] * 12 + i * 4) ~ key
        ss2[i] = emu:read32(address + 32 + pSel[3] * 12 + i * 4) ~ key
        ss3[i] = emu:read32(address + 32 + pSel[4] * 12 + i * 4) ~ key
    end
    
    -- If ppValue not specified, calculate max PP from move data
    if ppValue == nil then
        -- Get the move ID
        local moveId
        if moveSlot == 1 then
            moveId = ss1[0] & 0xFFFF
        elseif moveSlot == 2 then
            moveId = ss1[0] >> 16
        elseif moveSlot == 3 then
            moveId = ss1[1] & 0xFFFF
        elseif moveSlot == 4 then
            moveId = ss1[1] >> 16
        end
        
        -- Get the move name from the move array (add 1 because array is 1-indexed with empty at [1])
        local moveName = move[moveId + 1] or "Unknown"
        
        -- Look up base PP from our dictionary
        local basePP = movePPDict[moveName]
        if not basePP then
            console:log("Warning: No PP data for move '" .. moveName .. "' (ID: " .. moveId .. "), using default 10")
            basePP = 10
        end
        
        -- PP bonuses are stored as 2 bits per move in ss0[2]
        local ppBonuses = mon.ppBonuses
        local moveBonus = (ppBonuses >> ((moveSlot - 1) * 2)) & 0x3
        
        -- Calculate max PP: base + 20% per PP Up (up to 60% with 3 PP Ups)
        ppValue = math.floor(basePP * (1 + moveBonus * 0.2))
    end
    
    -- PP is stored in ss1[2], one byte per move slot:
    -- Move 1 PP: bits 0-7
    -- Move 2 PP: bits 8-15  
    -- Move 3 PP: bits 16-23
    -- Move 4 PP: bits 24-31
    if moveSlot == 1 then
        ss1[2] = (ss1[2] & 0xFFFFFF00) | (ppValue & 0xFF)
    elseif moveSlot == 2 then
        ss1[2] = (ss1[2] & 0xFFFF00FF) | ((ppValue & 0xFF) << 8)
    elseif moveSlot == 3 then
        ss1[2] = (ss1[2] & 0xFF00FFFF) | ((ppValue & 0xFF) << 16)
    elseif moveSlot == 4 then
        ss1[2] = (ss1[2] & 0x00FFFFFF) | ((ppValue & 0xFF) << 24)
    end
    
    -- Recalculate checksum
    local checksum = 0
    for i = 0, 2 do
        checksum = checksum + (ss0[i] & 0xFFFF) + (ss0[i] >> 16)
        checksum = checksum + (ss1[i] & 0xFFFF) + (ss1[i] >> 16)
        checksum = checksum + (ss2[i] & 0xFFFF) + (ss2[i] >> 16)
        checksum = checksum + (ss3[i] & 0xFFFF) + (ss3[i] >> 16)
    end
    
    -- Write checksum
    emu:write16(address + 28, checksum)
    
    -- Write back the encrypted substructures
    for i = 0, 2 do
        emu:write32(address + 32 + pSel[1] * 12 + i * 4, ss0[i] ~ key)
        emu:write32(address + 32 + pSel[2] * 12 + i * 4, ss1[i] ~ key)
        emu:write32(address + 32 + pSel[3] * 12 + i * 4, ss2[i] ~ key)
        emu:write32(address + 32 + pSel[4] * 12 + i * 4, ss3[i] ~ key)
    end
end

