function setBoxMon(address, newNature, IVs, moveSlot, moveName, level, species,
                   abilityNum, heldItem)
    local mon = readBoxMon(address)

    local key = mon.otId ~ mon.personality
    local substructSelector = {
        [0] = {0, 1, 2, 3},
        [1] = {0, 1, 3, 2},
        [2] = {0, 2, 1, 3},
        [3] = {0, 3, 1, 2},
        [4] = {0, 2, 3, 1},
        [5] = {0, 3, 2, 1},
        [6] = {1, 0, 2, 3},
        [7] = {1, 0, 3, 2},
        [8] = {2, 0, 1, 3},
        [9] = {3, 0, 1, 2},
        [10] = {2, 0, 3, 1},
        [11] = {3, 0, 2, 1},
        [12] = {1, 2, 0, 3},
        [13] = {1, 3, 0, 2},
        [14] = {2, 1, 0, 3},
        [15] = {3, 1, 0, 2},
        [16] = {2, 3, 0, 1},
        [17] = {3, 2, 0, 1},
        [18] = {1, 2, 3, 0},
        [19] = {1, 3, 2, 0},
        [20] = {2, 1, 3, 0},
        [21] = {3, 1, 2, 0},
        [22] = {2, 3, 1, 0},
        [23] = {3, 2, 1, 0}
    }
    local pSel = substructSelector[mon.personality % 24]

    local ss0 = {}
    local ss1 = {}
    local ss2 = {}
    local ss3 = {}
    for i = 0, 2 do
        ss0[i] = emu:read32(address + 32 + pSel[1] * 12 + i * 4) ~ key
        ss1[i] = emu:read32(address + 32 + pSel[2] * 12 + i * 4) ~ key
        ss2[i] = emu:read32(address + 32 + pSel[3] * 12 + i * 4) ~ key
        ss3[i] = emu:read32(address + 32 + pSel[4] * 12 + i * 4) ~ key
    end

    -- set all IVs to 31
    if (IVs ~= nil) then
        ss3[1] = (ss3[1] & 0x80000001) | (IVs[1] << 1) | (IVs[2] << 6) |
                     (IVs[3] << 11) | (IVs[6] << 16) | (IVs[4] << 21) |
                     (IVs[5] << 26)
    end

    -- set hiddenNature
    if (newNature ~= nil) then
        local natureIx = indexOf(nature, newNature)
        if (natureIx == nil) then
            console:log('Invalid nature')
            return
        end
        ss0[2] = (ss0[2] ~ (mon.hiddenNature << 16)) |
                     (((natureIx - 1) & 0x1F) << 16)
    end

    if (moveSlot ~= nil) then
        local moveIx = 0
        if (moveName ~= nil) then
            moveIx = indexOf(move, moveName) - 1
        end
        if (moveIx == nil) then
            console:log('Invalid move')
            return
        end
        if (moveSlot < 1 or moveSlot > 4) then
            console:log('Invalid move slot number')
            return
        end
        if moveSlot == 1 then
            ss1[0] = (ss1[0] & 0xFFFF0000) | moveIx
        elseif moveSlot == 2 then
            ss1[0] = (ss1[0] & 0x0000FFFF) | (moveIx << 16)
        elseif moveSlot == 3 then
            ss1[1] = (ss1[1] & 0xFFFF0000) | moveIx
        elseif moveSlot == 4 then
            ss1[1] = (ss1[1] & 0x0000FFFF) | (moveIx << 16)
        end
    end

    if (abilityNum ~= nil) then
        ss3[2] = (abilityNum << 29) | (ss3[2] & 0x03FFFFFF)
    end

    if (level ~= nil) then
        emu:write8(address + 84, level)
        ss0[1] = expRequired(mon.species, level)
    end

    if (species ~= nil) then
        local speciesIx = indexOf(mons, species)
        ss0[0] = (ss0[0] & 0xFFFF0000) | speciesIx
        ss0[1] = expRequired(speciesIx, calcLevel(mon.experience, speciesIx))
    end

    if (heldItem ~= nil) then
        local itemIx = indexOf(item, heldItem)
        ss0[0] = (ss0[0] & 0x0000FFFF) | (itemIx << 16)
    end

    -- fix checksum
    local checksum = 0
    for i = 0, 2 do
        checksum = checksum + (ss0[i] & 0xFFFF) + (ss0[i] >> 16)
        checksum = checksum + (ss1[i] & 0xFFFF) + (ss1[i] >> 16)
        checksum = checksum + (ss2[i] & 0xFFFF) + (ss2[i] >> 16)
        checksum = checksum + (ss3[i] & 0xFFFF) + (ss3[i] >> 16)
    end
    emu:write16(address + 28, checksum)

    for i = 0, 2 do
        emu:write32(address + 32 + pSel[1] * 12 + i * 4, ss0[i] ~ key)
        emu:write32(address + 32 + pSel[2] * 12 + i * 4, ss1[i] ~ key)
        emu:write32(address + 32 + pSel[3] * 12 + i * 4, ss2[i] ~ key)
        emu:write32(address + 32 + pSel[4] * 12 + i * 4, ss3[i] ~ key)
    end
end

function setPCBoxMon(address, newNature, IVs, moveSlot, moveName, level, species, abilityNum, heldItem)
    local mon = readBoxMon(address)
    local key = mon.otId ~ mon.personality

    local substructSelector = {
        [0] = {0, 1, 2, 3}, [1] = {0, 1, 3, 2}, [2] = {0, 2, 1, 3},
        [3] = {0, 3, 1, 2}, [4] = {0, 2, 3, 1}, [5] = {0, 3, 2, 1},
        [6] = {1, 0, 2, 3}, [7] = {1, 0, 3, 2}, [8] = {2, 0, 1, 3},
        [9] = {3, 0, 1, 2}, [10] = {2, 0, 3, 1}, [11] = {3, 0, 2, 1},
        [12] = {1, 2, 0, 3}, [13] = {1, 3, 0, 2}, [14] = {2, 1, 0, 3},
        [15] = {3, 1, 0, 2}, [16] = {2, 3, 0, 1}, [17] = {3, 2, 0, 1},
        [18] = {1, 2, 3, 0}, [19] = {1, 3, 2, 0}, [20] = {2, 1, 3, 0},
        [21] = {3, 1, 2, 0}, [22] = {2, 3, 1, 0}, [23] = {3, 2, 1, 0}
    }
    local pSel = substructSelector[mon.personality % 24]

    local ss0, ss1, ss2, ss3 = {}, {}, {}, {}
    for i = 0, 2 do
        ss0[i] = emu:read32(address + 32 + pSel[1]*12 + i*4) ~ key
        ss1[i] = emu:read32(address + 32 + pSel[2]*12 + i*4) ~ key
        ss2[i] = emu:read32(address + 32 + pSel[3]*12 + i*4) ~ key
        ss3[i] = emu:read32(address + 32 + pSel[4]*12 + i*4) ~ key
    end

    -- IVs
    if IVs then
        ss3[1] = (ss3[1] & 0x80000001)
        ss3[1] = ss3[1]
            | (IVs[1] << 1)
            | (IVs[2] << 6)
            | (IVs[3] << 11)
            | (IVs[6] << 16)
            | (IVs[4] << 21)
            | (IVs[5] << 26)
    end

    -- Nature
    if newNature then
        local natureIx = indexOf(nature, newNature)
        if natureIx then
            ss0[2] = (ss0[2] & 0xFE00FFFF) | (((natureIx - 1) & 0x1F) << 16)
        else
            console:log("❌ Nature invalide")
        end
    end

    -- Moves (simple ou multiple)
    if moveSlot and moveName then
        -- 🧩 Cas 1 : liste complète
        if type(moveName) == "table" then
            local slots = moveSlot or {1, 2, 3, 4}
            for i, mv in ipairs(moveName) do
                local slot = slots[i]
                if slot then
                    local moveIx = indexOf(move, mv)
                    if moveIx then
                        moveIx = moveIx - 1
                        if slot == 1 then ss1[0] = (ss1[0] & 0xFFFF0000) | moveIx
                        elseif slot == 2 then ss1[0] = (ss1[0] & 0x0000FFFF) | (moveIx << 16)
                        elseif slot == 3 then ss1[1] = (ss1[1] & 0xFFFF0000) | moveIx
                        elseif slot == 4 then ss1[1] = (ss1[1] & 0x0000FFFF) | (moveIx << 16)
                        end
                    else
                        console:log("⚠️ Move invalide : " .. tostring(mv))
                    end
                end
            end

        -- 🧩 Cas 2 : un seul move + slot
        elseif type(moveName) == "string" then
            local moveIx = indexOf(move, moveName)
            if moveIx then
                moveIx = moveIx - 1
                if moveSlot == 1 then ss1[0] = (ss1[0] & 0xFFFF0000) | moveIx
                elseif moveSlot == 2 then ss1[0] = (ss1[0] & 0x0000FFFF) | (moveIx << 16)
                elseif moveSlot == 3 then ss1[1] = (ss1[1] & 0xFFFF0000) | moveIx
                elseif moveSlot == 4 then ss1[1] = (ss1[1] & 0x0000FFFF) | (moveIx << 16)
                end
            else
                console:log("⚠️ Move invalide : " .. tostring(moveName))
            end
        end
    end

    -- Species
    if species then
        local speciesIx = indexOf(mons, species)
        if not speciesIx then
            console:log("❌ Espèce invalide : " .. tostring(species))
            return
        end
        ss0[0] = (ss0[0] & 0xFFFF0000) | speciesIx
    end

    -- Niveau (exp)
    if level then
        local speciesIx = mon.species
        if species then
            speciesIx = indexOf(mons, species)
        end
        local exp = expRequired(speciesIx, level)
        ss0[1] = exp
    end

    -- Held item
    if heldItem then
        local itemIx = indexOf(item, heldItem)
        ss0[0] = (ss0[0] & 0x0000FFFF) | (itemIx << 16)
    end

    -- Ability
    if abilityNum then
        ss3[2] = (abilityNum << 29) | (ss3[2] & 0x03FFFFFF)
    end

    -- 🔄 Fix checksum
    local checksum = 0
    for i = 0, 2 do
        checksum = checksum + (ss0[i] & 0xFFFF) + (ss0[i] >> 16)
        checksum = checksum + (ss1[i] & 0xFFFF) + (ss1[i] >> 16)
        checksum = checksum + (ss2[i] & 0xFFFF) + (ss2[i] >> 16)
        checksum = checksum + (ss3[i] & 0xFFFF) + (ss3[i] >> 16)
    end
    emu:write16(address + 28, checksum)

    -- 🔁 Réécrire les sous-structures
    for i = 0, 2 do
        emu:write32(address + 32 + pSel[1]*12 + i*4, ss0[i] ~ key)
        emu:write32(address + 32 + pSel[2]*12 + i*4, ss1[i] ~ key)
        emu:write32(address + 32 + pSel[3]*12 + i*4, ss2[i] ~ key)
        emu:write32(address + 32 + pSel[4]*12 + i*4, ss3[i] ~ key)
    end
end



