function forceLow16PID(slot, newLow16)
    local address = getSlotAddress(slot)
    if not address then
        console:log("Slot invalide")
        return
    end

    local mon = readBoxMon(address)
    local oldPID = mon.personality
    local oldHigh16 = (oldPID >> 16) & 0xFFFF
    local newPID = (oldHigh16 << 16) | (newLow16 & 0xFFFF)
    local oldNature = getNature(mon)
    local IVs = {
        mon.hpIV, mon.attackIV, mon.defenseIV,
        mon.spAttackIV, mon.spDefenseIV, mon.speedIV
    }

    -- Écriture du nouveau PID
    emu:write32(address, newPID)

    -- Recalculer la clé d'encodage
    local oldKey = mon.otId ~ oldPID
    local newKey = mon.otId ~ newPID

    -- Substructure selector
    local substructSelector = {
        [0] = {0, 1, 2, 3}, [1] = {0, 1, 3, 2}, [2] = {0, 2, 1, 3}, [3] = {0, 3, 1, 2},
        [4] = {0, 2, 3, 1}, [5] = {0, 3, 2, 1}, [6] = {1, 0, 2, 3}, [7] = {1, 0, 3, 2},
        [8] = {2, 0, 1, 3}, [9] = {3, 0, 1, 2}, [10] = {2, 0, 3, 1}, [11] = {3, 0, 2, 1},
        [12] = {1, 2, 0, 3}, [13] = {1, 3, 0, 2}, [14] = {2, 1, 0, 3}, [15] = {3, 1, 0, 2},
        [16] = {2, 3, 0, 1}, [17] = {3, 2, 0, 1}, [18] = {1, 2, 3, 0}, [19] = {1, 3, 2, 0},
        [20] = {2, 1, 3, 0}, [21] = {3, 1, 2, 0}, [22] = {2, 3, 1, 0}, [23] = {3, 2, 1, 0}
    }

    local oldSel = substructSelector[oldPID % 24]
    local newSel = substructSelector[newPID % 24]
    local ss = {}

    -- Lire les substructs décryptés avec l'ancienne clé
    for idx = 1, 4 do
        ss[idx] = {}
        for i = 0, 2 do
            local val = emu:read32(address + 32 + oldSel[idx] * 12 + i * 4) ~ oldKey
            ss[idx][i] = val
        end
    end

    -- Réécrire les substructs avec la nouvelle clé
    for idx = 1, 4 do
        for i = 0, 2 do
            local val = ss[idx][i] ~ newKey
            emu:write32(address + 32 + newSel[idx] * 12 + i * 4, val)
        end
    end

    -- Recalculer le checksum
    local checksum = 0
    for i = 1, 4 do
        for j = 0, 2 do
            local val = ss[i][j]
            checksum = checksum + (val & 0xFFFF) + (val >> 16)
        end
    end
    emu:write16(address + 28, checksum)

    -- Rétablir la nature
    setNature(slot, oldNature)

    console:log(string.format("PID modifié (slot %d) : 0x%08X — Nature conservée : %s", slot, newPID, oldNature))
end



function forceHigh16PID(slot, newHigh16)
