    local oldNature = getNature(mon)

    -- Appliquer le nouveau PID
    emu:write32(address, newPID)

    -- Recalculer les clés et positions
    local oldKey = mon.otId ~ oldPID
    local newKey = mon.otId ~ newPID

    local substructSelector = {
        [0] = {0, 1, 2, 3}, [1] = {0, 1, 3, 2}, [2] = {0, 2, 1, 3}, [3] = {0, 3, 1, 2},
        [4] = {0, 2, 3, 1}, [5] = {0, 3, 2, 1}, [6] = {1, 0, 2, 3}, [7] = {1, 0, 3, 2},
        [8] = {2, 0, 1, 3}, [9] = {3, 0, 1, 2}, [10] = {2, 0, 3, 1}, [11] = {3, 0, 2, 1},
