        [20] = {2, 1, 3, 0}, [21] = {3, 1, 2, 0}, [22] = {2, 3, 1, 0}, [23] = {3, 2, 1, 0}
    }

    local oldSel = substructSelector[oldPID % 24]
    local newSel = substructSelector[newPID % 24]
    local ss = {}

    -- Lire et décrypter les substructs existants avec l'ancienne clé
    for idx = 1, 4 do
        ss[idx] = {}
        for i = 0, 2 do
            local val = emu:read32(address + 32 + oldSel[idx] * 12 + i * 4) ~ oldKey
            ss[idx][i] = val
        end
    end

    -- Réécrire avec le nouveau PID et nouvelle clé
    for idx = 1, 4 do
        for i = 0, 2 do
            local val = ss[idx][i] ~ newKey
            emu:write32(address + 32 + newSel[idx] * 12 + i * 4, val)
        end
    end

    -- Fixer le checksum
    local checksum = 0
    for i = 1, 4 do
        for j = 0, 2 do
            local raw = ss[i][j]
            checksum = checksum + (raw & 0xFFFF) + (raw >> 16)
        end
    end
    emu:write16(address + 28, checksum)

    -- Rétablir la nature si elle a changé
    setNature(slot, oldNature)
    newPID = readBoxMon(address).personality
    console:log(string.format(
        "PID modifié: 0x%08X (haut 16 bits = 0x%04X). Nature: %s, IVs conservés.",
        newPID, newHigh16, natureName))
end

