function pickPokemonFromPool(pool)
    if #pool == 0 then return nil end

    local totalRate = 0
    for _, entry in ipairs(pool) do totalRate = totalRate + entry.rate end
    local rand = math.random() * totalRate
    local cumulative = 0

    for _, entry in ipairs(pool) do
        cumulative = cumulative + entry.rate
        if rand <= cumulative then
            return entry
        end
    end

    return pool[#pool]
end

