function rollZone(zoneData, tempPools, slotIndex)
    local zoneName = zoneData.name
    if depletedZones[zoneName] then
        console:log("Zone déjà épuisée : " .. zoneName)
        return nil
    end

    local pool = tempPools[zoneName]
    if not pool or #pool == 0 then
        console:log("Aucun Pokémon restant dans la zone : " .. zoneName)
        depletedZones[zoneName] = true
        return nil
    end

    -- Vérification Magnet Pull / Static
    local magnetPullActive, staticActive = hasMagnetPullOrStatic()
    if magnetPullActive or staticActive then
        local filteredPool = {}
        local targetType = magnetPullActive and "Steel" or "Electric"
        for _, entry in ipairs(pool) do
            local types = pokemonTypes[entry.name]
            if types and tableContains(types, targetType) then
                table.insert(filteredPool, entry)
            end
        end
        if #filteredPool > 0 and math.random() < 0.5 then
            pool = filteredPool
            console:log(string.format("%s réussi sur %s (%d résultats)",
                magnetPullActive and "Magnet Pull" or "Static", zoneName, #filteredPool))
        end
    end

    -- Repel Manip (si activé)
    if zoneData.repel then
        local adjusted = {}
        for _, entry in ipairs(pool) do
            local newEntry = deepcopy(entry)
            if type(entry.level) == "table" and entry.level.min then
                newEntry.level = entry.level.max
            end
            table.insert(adjusted, newEntry)
        end
        pool = adjusted
    end

    -- Tirage
    local chosenEntry = pickPokemonFromPool(pool)
    if not chosenEntry then
        depletedZones[zoneName] = true
        console:log("Aucun Pokémon trouvé pour " .. zoneName)
        return nil
    end

    local pokemon = chosenEntry.name
    local level = chosenEntry.level
    if type(level) == "table" then
        level = math.random(level.min, level.max)
    end

    local abilityNum = math.random(0, 1)
    local familyId = getDupeFamily(pokemon)

    obtained[pokemon] = {
        zone = zoneName,
        level = level,
        ability = abilityNum,
        slot = slotIndex
    }

    if familyId then
        obtainedFamilies[familyId] = true
        removeFamilyFromTempPools(tempPools, familyId)
    end

    console:log(string.format("Slot %d | %s → %s (niveau %s, ability %d, famille %s)",
        slotIndex + 1, zoneName, pokemon, level, abilityNum, familyId or "aucune"))

    return pokemon, level
end

-- =====================================================
-- GET DUPE FAMILY (selon mode)
-- =====================================================

function getDupeFamily(pokemon)
    local family = pokemonDupes[pokemon]
    local regional = dupesRegional[pokemon]
    if regional then
        return regional[dupeMode:gsub(" ", "_")] or regional.both
    end
    return family
end

-- =====================================================
-- MAGNET PULL & STATIC
-- =====================================================

function hasMagnetPullOrStatic()
    local magnetPullActive, staticActive = false, false
    for poke, data in pairs(obtained) do
        local abil = data.ability
        if magnetPullUsers[poke] then
            for _, a in ipairs(magnetPullUsers[poke]) do
                if abil == a then magnetPullActive = true end
            end
        end
        if staticUsers[poke] then
            for _, a in ipairs(staticUsers[poke]) do
                if abil == a then staticActive = true end
            end
        end
    end
    return magnetPullActive, staticActive
end

-- =====================================================
-- TIRAGE GLOBAL DE TOUTES LES ZONES DU PRESET
-- =====================================================

function rollZonesInOrder()
    console:log("Début du roll complet pour le preset " .. settings.preset_name)
    local tempPools = {}
    for _, z in ipairs(zonesToRoll) do
        tempPools[z.name] = deepcopy(z.encounters)
    end

    local slotIndex = 0
    for _, zoneData in ipairs(zonesToRoll) do
        rollZone(zoneData, tempPools, slotIndex)
        slotIndex = slotIndex + 1
    end

    console:log("\nRésumé final :")
    for poke, d in pairs(obtained) do
        console:log(string.format("- %s (zone %s, niveau %d, slot %d)", poke, d.zone, d.level, d.slot))
    end
end

-- =====================================================
-- UTILITAIRES
-- =====================================================

function deepcopy(orig)
    local copy = {}
    for k, v in pairs(orig) do
        if type(v) == "table" then copy[k] = deepcopy(v)
        else copy[k] = v end
    end
    return copy
end

function tableContains(tbl, val)
    for _, v in ipairs(tbl) do if v == val then return true end end
    return false
end

function removeFamilyFromTempPools(tempPools, familyId)
    for zone, pool in pairs(tempPools) do
        local i = 1
        while i <= #pool do
            local p = pool[i].name
            local fam = getDupeFamily(p)
            if fam == familyId then table.remove(pool, i)
            else i = i + 1 end
        end
        if #pool == 0 then depletedZones[zone] = true end
    end
end

-- =====================================================
--  CRÉATION DES POKÉMONS DANS LE PC
-- =====================================================

function createObtainedPokemon()
    local created = 0

    for pokemon, data in pairs(obtained) do
        local address = PC_BASE + 4 + (data.slot * POKEMON_SIZE)

        -- Récupération du moveset en fonction du niveau et du contexte
        local movesetData = getMovesetFor(pokemon, data.level, settings.context)
        local species = (movesetData and movesetData.finalEvolution) or pokemon
        local moves = (movesetData and movesetData.moves) or {}

        -- Tirage de la nature et des IVs
        local roll = getRandomNatureAndIVs()

        -- Application unique : setPCBoxMon
        setPCBoxMon(
            address,
            roll.nature,      -- Nature
            roll.ivs,         -- IVs
            {1,2,3,4},        -- Slots de move
            moves,            -- Liste des moves
            data.level,       -- Niveau
            species,          -- Espèce (ou évolution finale)
            data.ability,     -- Ability choisie
            nil               -- Aucun objet tenu
        )

        console:log(string.format(
            "Slot %d → %s lv.%d (%s) | Nature: %s | IVs: %s",
            data.slot + 1,
            species,
            data.level,
            table.concat(moves, ", "),
            roll.nature,
            table.concat(roll.ivs, "/")
        ))

        created = created + 1
    end

    console:log(string.format("✅ %d Pokémon créés dans le PC", created))
end


function getMovesetFor(pokemonName, level, context)
    local sets = data.movesets[pokemonName]
    if not sets then
        console:log("Aucun moveset trouvé pour " .. pokemonName)
        return nil
    end

    -- Filtrer les sets compatibles avec le contexte et le niveau
    local validSets = {}
    for _, set in ipairs(sets) do
        local ctxOK = (not set.context) or (set.context == context)
        local levelOK = (not set.minLevel) or (set.minLevel <= level)
        if ctxOK and levelOK then
            table.insert(validSets, set)
        end
    end

    if #validSets == 0 then
        console:log(string.format("Aucun moveset valide pour %s (niveau %d, contexte %s)",
            pokemonName, level or 0, context or "inconnu"))
        return nil
    end

    -- Choisir un set aléatoire parmi les valides
    local chosen = validSets[math.random(1, #validSets)]
    console:log(string.format("Moveset choisi pour %s : niveau ≥ %d (%s)",
        pokemonName, chosen.minLevel or 1, chosen.context or "Global"))

    -- Structuration du retour
    return {
        minLevel = chosen.minLevel or level,
        context = chosen.context or context,
        moves = chosen.moves or {},
        finalEvolution = chosen.finalEvolution or pokemonName
    }
end

function getRandomNatureAndIVs()
    -- Génère 6 IVs aléatoires (0–31)
    local ivs = {}
    for i = 1, 6 do
        ivs[i] = math.random(0, 31)
    end

    -- Tire une nature aléatoire
    local randomNature = nature[math.random(1, #nature)]

    -- Retourne les infos pour utilisation ailleurs
    return {
        nature = randomNature,
        ivs = ivs
    }
end

function randomizeBox()
        rollZonesInOrder()
        createObtainedPokemon()
end




function getHiddens(buffer)
    address = storageLoc + 4
    i = 0
    buffer:clear()
    for _, mon in ipairs(getParty()) do
        if (mon.species ~= 0) then
            buffer:print(string.format("%s - %s\n", mons[mon.species],
                                       getHP(mon)))
        end
    end
    while i < 120 do
        if (emu:read32(address) ~= 0) then
            buffer:print(string.format("%s - %s\n",
                                       mons[readBoxMon(address).species],
                                       getHP(readBoxMon(address))))
        end
        i = i + 1
        address = address + 80
    end
end

function hiddens()
    if not hiddenBuffer then
        console:log("error")
        return
    end
    getHiddens(hiddenBuffer)
end

